mapper.select:
SELECT id, db, host, username, password, port, created_at, updated_at
FROM database_connection_info
WHERE id = #{id}

mapper.update:
UPDATE database_connection_info
SET db = #{db}, host = #{host}, username = #{username}, password = #{password}, port = #{port}, created_at = #{createdAt}, updated_at = #{updatedAt}
WHERE id = #{id}
